cument.title == msg ? ' ' : msg; };  var clear = function() {    clearInterval(timeoutId);    document.title = oldTitle;    window.onmousemove = null;    timeoutId = null;  };    function addmsg(newcomment, newmessage){// use Jquery to create live elements //$("<div></div>").html('<p id="boxtext" style="font-weight:bold;" class="usertext">'+username+'</p><p style="display:inline-block;float:right;font-size:9pt;color:#CCCCCC;pading-right:8px;">'+date+'</p><p class="usertext" id="boxtext" class="text">'+text+'</p>').appendTo("#message");//$("#container").animate({"scrollTop": $('#messages_viewconversation')[0].scrollHeight}, "slow");	//alert(newcomment);		//alert(newmessage);	var lastcomment = $('#soundHandle_comment').val();	var lastmessage = $('#soundHandle_message').val();		if(lastcomment != newcomment && !!lastcomment) {			  soundHandle_comment.src = '/alertcomment.mp3';			  //    document.title = "("+newcomment+")shufflebuy";					soundHandle_comment.play();	//				  msg = newcomment+"comment";	//				  document.title = setInterval(blink, 2000);//						window.onmousemover = clear;			  }			  		if(lastmessage != newmessage && !!lastmessage) {			  soundHandle_message.src = '/alertmessage.mp3';			  soundHandle_message.play();			     if (!timeoutId) {			  msg = newmessage+" new msg";			    timeoutId = setInterval(blink, 2000);      window.onmousemove = clear;						}			  			   									}			      $('#soundHandle_comment').val(newcomment);    $('#soundHandle_message').val(newmessage);//	alert( $('#soundHandle_comment').val());	$('.checkbid').addClass('animated pulse');	$("#notifycount")[0].innerHTML = newcomment;    $('.checkbid').attr('src','images/notifybubblered2.png'); 	$('.checkinbox').addClass('animated pulse');	$("#inboxcount")[0].innerHTML = newmessage;    $('.checkinbox').attr('src','images/envmessage2.png'); 		        			            }    function waitForMsg(){        /* This requests the url "msgsrv.php"        When it complete (or errors)*/        $.ajax({            type: "POST",            url: "get_commentcount.php",            async: true, /* If set to non-async, browser shows page as "Loading.."*/            cache: false,            timeout:20000, /* Timeout in ms */            success: function(data){ /* called when request to barge.php completes */			//	alert(data);			   // var myArr = $.parseJSON(data);			               var arr = eval('(' + data + ')');   //   alert(arr);				var newcomment = arr.newcomment;			var newmessage_id = arr.newmessage_id;			var newmessage = arr.newmessage;		//	alert(newmessage_id);				//		alert(arr.newmessage);            addmsg(newcomment, newmessage); /* Add response to a .msg div (with the "new" class)*/			  setTimeout(                    waitForMsg, /* Request next message */                    10000 /* ..after 1 seconds */                );            },            error: function(XMLHttpRequest, textStatus, errorThrown){                addmsg("error", textStatus + " (" + errorThrown + ")");                setTimeout(                    waitForMsg, /* Try again after.. */                    10000); /* milliseconds (15seconds) */            }        });    };    $(document).ready(function(){        waitForMsg(); /* Start the inital request */    });    </script>      		<script type="text/javascript">$(function() {$(".view_comments").click(function() {var ID = $(this).attr("id");$.ajax({type: "POST",url: "viewajax.php",data: "msg_id="+ ID, cache: false,success: function(html){$("#view_comments"+ID).prepend(html);$("#view"+ID).remove();$("#two_comments"+ID).remove();}});return false;});});</script><script type="text/javascript">$(function() {$(".checkbid").click(function() {var ID = $(this).attr("id");$.ajax({type: "POST",url: "checkedbid.php",data: "bidid="+ ID, cache: false,success: function(data){  clearconsole();}});return false;setInt